mapper.select:
/** com.pharm.chorok.web.admin.repository.ADAdminRepository.selectAdmin */ SELECT USR_NO, USR_EML, USR_PWD, USR_NM, USR_PHN_NO, DATA_OWNR_ID, USR_GRADE, (SELECT DITC_NM FROM TB_COMM_CODE WHERE GRP_CD = 'C1003' AND DITC_CD = USR_GRADE AND USE_YN = 'Y') AS USR_GRADE_VAL, USR_AUTH, (SELECT DITC_NM FROM TB_COMM_CODE WHERE GRP_CD = 'C1016' AND DITC_CD = USR_AUTH AND USE_YN = 'Y') AS USR_AUTH_VAL, USR_APRV, (SELECT DITC_NM FROM TB_COMM_CODE WHERE GRP_CD = 'C1010' AND DITC_CD = USR_APRV AND USE_YN = 'Y') AS USR_APRV_VAL, DEL_YN, REG_USR_NO,
DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT, UPD_USR_NO, DATE_FORMAT(UPD_DT, '%Y-%m-%d') AS UPD_DT FROM TB_COMM_USER
(USR_NM LIKE CONCAT('%',#{srchTxt},'%') OR USR_EML LIKE CONCAT('%',#{srchTxt},'%'))
USR_NM LIKE CONCAT('%',#{srchTxt},'%')
USR_EML LIKE CONCAT('%',#{srchTxt},'%')
AND USR_AUTH = #{cbAuth}
AND USR_APRV = #{cbAppv}

mapper.update:
UPDATE TB_COMM_USER
SET USR_EML = #{usrEml}
  , USR_PWD = #{usrPwd}
  , USR_NM = #{usrNm}
  , USR_PHN_NO = #{usrPhnNo}
  , DATA_OWNR_ID = #{dataOwnrId}
  , USR_GRADE = #{usrGrade}
  , USR_AUTH = #{usrAuth}
  , USR_APRV = #{usrAprv}
  , DEL_YN = #{delYn}
  , UPD_DT = NOW()
  , UPD_USR_NO = #{updUsrNo}
WHERE USR_NO = ${usrNo}

UPDATE TB_COMM_USER
SET USR_PWD = #{usrPwd}
WHERE USR_NO = ${usrNo}

UPDATE TB_COMM_USER
SET DEL_YN = 'Y'
WHERE USR_NO = ${usrNo}

mapper.select:
SELECT COUNT(USR_NO)
FROM TB_COMM_USER
WHERE USR_EML = #{usrEml}

SELECT COUNT(USR_NO)
FROM TB_COMM_USER
WHERE USR_PHN_NO = #{usrPhnNo}

SELECT COUNT(USR_NO)
FROM TB_COMM_USER
WHERE USR_EML = #{usrEml}
AND USR_NO != ${usrNo}

SELECT COUNT(USR_NO)
FROM TB_COMM_USER
WHERE USR_PHN_NO = #{usrPhnNo}
AND USR_NO != ${usrNo}
