mapper.select:
/** com.pharm.chorok.web.main.repository.CustomerRepository.findCustomerByDosgDt */ /** 금일 복용상담이 잡혀있는 고객정보 조회*/
SELECT CUST.CUST_ID , CUST.CUST_USR_NM , CASE LENGTH(CUST.CUST_CELL_NO) WHEN 11 THEN CONCAT(LEFT(CUST.CUST_CELL_NO, 3), '-', MID('***********', 4, 4), '-', RIGHT(CUST.CUST_CELL_NO, 4)) WHEN 10 THEN CONCAT(LEFT(CUST.CUST_CELL_NO, 3), '-', MID('**********' , 4, 3), '-', RIGHT(CUST.CUST_CELL_NO, 4)) ELSE '' END AS CUST_CELL_NO , CUST.ZIP_CODE , CUST.ADDR1 , CASE WHEN CUST.CUST_GEN_TP_CD = 'F' THEN '여' WHEN CUST.CUST_GEN_TP_CD = 'M' THEN '남' ELSE '' END AS CUST_GEN_TP_NM , CCHART.* , CASE WHEN CCHART.CNST_HH_CD IS NULL THEN '' WHEN CCHART.CNST_HH_CD = '' THEN '' ELSE CONCAT(CCHART.CNST_HH_CD, '시') END CNST_HH_NM , CCHART.CNST_ID , (SELECT T.DITC_NM FROM TB_COMM_CODE T WHERE T.DITC_CD = DCHART.PAUS_YN AND T.GRP_CD = 'C1021') AS PAUS_YN , (SELECT T.USR_NM FROM TB_COMM_USER T WHERE CCHART.PIC_USR_NO = T.USR_NO) AS PIC_USR_NM , (SELECT T.USR_NM FROM TB_COMM_USER T WHERE CCHART.PIC2_USR_NO = T.USR_NO) AS PIC2_USR_NM , (SELECT T.DITC_NM FROM TB_COMM_CODE T WHERE T.DITC_CD = CCHART.DOSG_TP_CD AND T.GRP_CD = 'C1018') AS DOSG_TP_NM , DATE_FORMAT( STR_TO_DATE(DCHART.DOSG_DT, '%Y%m%d'), '%Y-%m-%d') AS DOSG_DT , DCHART.DOSG_SEQ FROM TB_CUSTOMER CUST INNER JOIN TB_PP_CNST_CHART CCHART ON CUST.CUST_ID = CCHART.CUST_ID INNER JOIN TB_PP_DOSG_CHART DCHART ON CCHART.CNST_ID = DCHART.CNST_ID WHERE 1 = 1 AND DCHART.DOSG_DT = DATE_FORMAT(#{dosgDt}, '%Y%m%d')
AND CCHART.PIC_USR_NO = #{picUsrNo}
AND CCHART.PIC2_USR_NO = #{pic2UsrNo}
AND DCHART.PAUS_YN = #{pausYn}
AND DCHART.CALL_YN = #{callYn}
AND DCHART.CALL_YN IN ('CHEMIST', 'COUNSELOR')
ORDER BY CCHART.CNST_HH_CD ASC

/** com.pharm.chorok.web.main.repository.CustomerRepository.findAllCustomer */ /** 고객목록 조회 */
SELECT CUST_ID , CUST_USR_NM , CUST_CELL_NO , CUST_BIRTH_DT , CUST_GEN_TP_CD , MRG_YN , PCRT_CHD_CNT , LST_PCRT_YEAR , BRST_FDG_YN , VIST_TP_CD , ZIP_CODE , ADDR1 , ADDR2 , DEL_YN , REG_DT , REG_USR_NO , UPD_DT , UPD_USR_NO , CUST_MEMO , CUST_MEMO2 , RCMD_CUST_ID , RCMD_CUST_NM , RCMD_CELL_NO , (SELECT DITC_NM FROM TB_COMM_CODE WHERE GRP_CD = 'C1008' AND DITC_CD = CUST_GEN_TP_CD AND USE_YN = 'Y') AS CUST_GEN_TP_CD_VAL , (SELECT DITC_NM FROM TB_COMM_CODE WHERE GRP_CD = 'C1020' AND DITC_CD = MRG_YN AND USE_YN = 'Y') AS MRG_YN_VAL , (SELECT COUNT(*) FROM TB_CUSTOMER WHERE RCMD_CUST_ID = A.CUST_ID) AS RCMD_CNT FROM TB_CUSTOMER A WHERE 1 = 1
AND DEL_YN = #{delYn}
AND CUST_USR_NM LIKE CONCAT('%', #{custUsrNm}, '%')
AND CUST_CELL_NO LIKE CONCAT('%', #{custCellNo}, '%')
ORDER BY CUST_USR_NM

mapper.insert:

INSERT INTO TB_CUSTOMER (
       CUST_ID
     , CUST_USR_NM
     , CUST_CELL_NO
     , CUST_BIRTH_DT
     , CUST_GEN_TP_CD
     , MRG_YN
     , PCRT_CHD_CNT
     , LST_PCRT_YEAR
     , BRST_FDG_YN
     , VIST_TP_CD
     , ZIP_CODE
     , ADDR1
     , ADDR2
     , DEL_YN
     , REG_DT
     , REG_USR_NO
     , CUST_MEMO
     , CUST_MEMO2
     , RCMD_CUST_ID
     , RCMD_CUST_NM
     , RCMD_CELL_NO
) VALUES (
       #{custId}
     , #{custUsrNm}
     , #{custCellNo}
     , #{custBirthDt}
     , #{custGenTpCd}
     , #{mrgYn}
     , #{pcrtChdCnt}
     , #{lstPcrtYear}
     , #{brstFdgYn}
     , #{vistTpCd}
     , #{zipCode}
     , #{addr1}
     , #{addr2}
     , #{delYn}
     , now()
     , #{regUsrNo}
     , #{custMemo}
     , #{custMemo2}
     , #{rcmdCustId}
     , #{rcmdCustNm}
     , #{rcmdCellNo}
)

mapper.update:

UPDATE TB_CUSTOMER
   SET CUST_USR_NM     = #{custUsrNm}
     , CUST_CELL_NO    = #{custCellNo}
     , CUST_BIRTH_DT   = #{custBirthDt}
     , CUST_GEN_TP_CD  = #{custGenTpCd}
     , MRG_YN          = #{mrgYn}
     , PCRT_CHD_CNT    = #{pcrtChdCnt}
     , LST_PCRT_YEAR   = #{lstPcrtYear}
     , BRST_FDG_YN     = #{brstFdgYn}
     , VIST_TP_CD      = #{vistTpCd}
     , ZIP_CODE        = #{zipCode}
     , ADDR1           = #{addr1}
     , ADDR2           = #{addr2}
     , DEL_YN		   = #{delYn}
     , UPD_DT          = now()
     , UPD_USR_NO      = #{regUsrNo}
     , CUST_MEMO 	   = #{custMemo}
     , CUST_MEMO2 	   = #{custMemo2}
     , RCMD_CUST_ID    = #{rcmdCustId}
     , RCMD_CUST_NM    = #{rcmdCustNm}
     , RCMD_CELL_NO    = #{rcmdCellNo}
 WHERE CUST_ID = #{custId}
