mapper.insert:

INSERT INTO TB_PP_RSVT_SCH (
     RSVT_USR_NM
   , RSVT_DT
   , RSVT_CELL_NO
   , RSVT_PHN_NO
   , RSVT_TP_CD
   , RSVT_DESC
   , GEN_TP_CD
   , RCMD_USR_NO
   , PIC_USR_NO
   , CUST_ID
   , REG_DT
   , REG_USR_NO
) VALUES (
     #{rsvtUsrNm}
   , STR_TO_DATE( #{rsvtDt}, '%Y-%m-%d %H:%i:%s')
   , REPLACE( #{rsvtCellNo}, '-', '')
   , REPLACE( #{rsvtPhnNo} , '-', '') 
   , #{rsvtTpCd}
   , #{rsvtDesc}
   , #{genTpCd}
   , #{rcmdUsrNo}
   , #{picUsrNo}
   , #{custId}
   , now()
   , #{regUsrNo}
)

mapper.update:

	UPDATE TB_PP_RSVT_SCH
	   SET UPD_DT        = now()
		 , UPD_USR_NO    = #{updUsrNo}
		 , CUST_ID       = #{custId}
     WHERE RSVT_ID = #{rsvtId}


	UPDATE TB_PP_RSVT_SCH
	   SET UPD_DT        = now()
		 , UPD_USR_NO    = #{updUsrNo}
		 , RSVT_USR_NM   = #{rsvtUsrNm}
		 , RSVT_DT       = STR_TO_DATE( #{rsvtDt}, '%Y-%m-%d %H:%i:%s')
		 , RSVT_CELL_NO  = REPLACE( #{rsvtCellNo}, '-', '') 
		 , RSVT_PHN_NO   = REPLACE( #{rsvtPhnNo} , '-', '') 
		 , RSVT_TP_CD    = #{rsvtTpCd} 
		 , RSVT_DESC     = #{rsvtDesc} 
		 , GEN_TP_CD     = #{genTpCd} 
		 , RCMD_USR_NO   = #{rcmdUsrNo} 
	     , PIC_USR_NO    = #{picUsrNo}
		 , CUST_ID       = #{custId}
     WHERE RSVT_ID = #{rsvtId}
