mapper.insert:

INSERT INTO TB_PP_RSVT_SCH (
     ID,
   , RSVT_USR_NM
   , RSVT_DT
   , RSVT_CELL_NO
   , RSVT_PHN_NO
   , RSVT_TP_CD
   , RSVT_DESC
   , GEN_TP_CD
   , RCMD_USR_NO
   , PIC_USR_NO
   , REG_DT
   , REG_USR_NO
) VALUES
     #{id}
   , #{rsvtUsrNm}
   , STR_TO_DATE( #{rsvtDt}, '%m/%d/%Y %H:%i:%s')
   , #{rsvtCellNo}
   , #{rsvtPhnNo}
   , #{rsvtTpCd}
   , #{rsvtDesc}
   , #{genTpCd}
   , #{rcmdUsrNo}
   , #{picUsrNo}
   , now()
   , #{regUsrNo}
);

mapper.update:

	UPDATE TB_PP_RSVT_SCH
	   SET UPD_DT        = now()
		 , UPD_USR_NO    = #{updUsrNo}
		 , RSVT_USR_NM   = #{rsvtUsrNm} 
		 , RSVT_DT       = STR_TO_DATE( #{rsvtDt}, '%m/%d/%Y %H:%i:%s')
		 , RSVT_CELL_NO  = #{rsvtCellNo} 
		 , RSVT_PHN_NO   = #{rsvtPhnNo} 
		 , RSVT_TP_CD    = #{rsvtTpCd} 
		 , RSVT_DESC     = #{rsvtDesc} 
		 , GEN_TP_CD     = #{genTpCd} 
		 , RCMD_USR_NO   = #{rcmdUsrNo} 
	     , PIC_USR_NO    = #{picUsrNo}
     WHERE ID = #{id}
