mapper.update:
UPDATE TB_CUSTOMER
SET CUST_USR_NM = #{custUsrNm}
  , CUST_CELL_NO = #{custCellNo}
  , CUST_BIRTH_DT = #{custBirthDt}
  , CUST_GEN_TP_CD = #{custGenTpCd}
  , DEL_YN = #{delYn}
  , UPD_DT = NOW()
  , UPD_USR_NO = #{updUsrNo}
WHERE CUST_ID = ${custId}

UPDATE TB_CUSTOMER
SET DEL_YN = 'Y'
WHERE CUST_ID = ${custId}

mapper.select:
SELECT COUNT(CUST_ID)
FROM TB_CUSTOMER
WHERE CUST_CELL_NO = #{custCellNo}

SELECT COUNT(CUST_ID)
FROM TB_CUSTOMER
WHERE CUST_CELL_NO = #{custCellNo}
AND CUST_ID != ${custId}
